// import React from 'react';

var arr = ["X", "Y", "Z"];

function Shuffle() {

    displayRun();

    let row1 = document.getElementById("row1").value;
    let row2 = document.getElementById("row2").value;
    let row3 = document.getElementById("row3").value;

    let recentDisplay = (row1 + " - " + row2 + " - " + row3);

    if (row1 === row2 && row2 === row3) {
        recentDisplay = "*" + recentDisplay + "* \n";
    }
    else {
        recentDisplay = recentDisplay + "\n";
    }

    document.getElementById("lastOutput").value = recentDisplay + document.getElementById("lastOutput").value;

}

function displayRun() {

    for (let i = 0; i < 10; i++) {
        document.getElementById("row1").value = arr[Math.floor(Math.random() * arr.length)];
        document.getElementById("row2").value = arr[Math.floor(Math.random() * arr.length)];
        document.getElementById("row3").value = arr[Math.floor(Math.random() * arr.length)];
    }

    console.log(document.getElementById("row1").value + document.getElementById("row2").value + document.getElementById("row3").value);
}


export default Shuffle;
