import React from 'react';
import './App.css';

var arr = ["X", "Y", "Z"];

function Shuffle() {

  displayRun();

  let recentDisplay = (document.getElementById("row1").value + document.getElementById("row2").value + document.getElementById("row3").value + "\n");

  document.getElementById("lastOutput").value = recentDisplay + document.getElementById("lastOutput").value;
}

function displayRun() {

  for (let i = 0; i < 10; i++) {
    document.getElementById("row1").value = arr[Math.floor(Math.random() * arr.length)];
    document.getElementById("row2").value = arr[Math.floor(Math.random() * arr.length)];
    document.getElementById("row3").value = arr[Math.floor(Math.random() * arr.length)];
  }

  console.log(document.getElementById("row1").value + document.getElementById("row2").value + document.getElementById("row3").value);
}

function App() {
  return (
    <>
      <div className="slotMachine">
        <div className="slotDisplay">
          <input id="row1" type="text" value="X" disabled />
          <input id="row2" type="text" value="Y" disabled />
          <input id="row3" type="text" value="Z" disabled />
        </div>
        <button id="shuffle" onClick={Shuffle}>SHUFFLE</button>
      </div>

      <textarea id="lastOutput" rows="4" cols="20" disabled></textarea>

    </>
  );
}

export default App;
